import React, { useState } from 'react';
import { motion } from 'framer-motion';

const HealthHub = () => {
  const [activeTab, setActiveTab] = useState('overview');
  const [showAddRecord, setShowAddRecord] = useState(false);

  const healthMetrics = [
    { name: 'Blood Pressure', value: '120/80', unit: 'mmHg', trend: 'stable' },
    { name: 'Heart Rate', value: '72', unit: 'bpm', trend: 'normal' },
    { name: 'Blood Sugar', value: '95', unit: 'mg/dL', trend: 'good' },
    { name: 'Weight', value: '68', unit: 'kg', trend: 'stable' },
  ];

  const medications = [
    { name: 'Metformin', dosage: '500mg', frequency: 'Twice daily', lastTaken: '2 hours ago' },
    { name: 'Lisinopril', dosage: '10mg', frequency: 'Once daily', lastTaken: '4 hours ago' },
    { name: 'Aspirin', dosage: '81mg', frequency: 'Once daily', lastTaken: '6 hours ago' },
  ];

  const appointments = [
    { date: 'March 15, 2024', time: '10:00 AM', doctor: 'Dr. Smith', type: 'Regular Checkup' },
    { date: 'April 2, 2024', time: '2:30 PM', doctor: 'Dr. Johnson', type: 'Specialist Visit' },
  ];

  return (
    <div className="p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Health Hub</h1>
          <button
            onClick={() => setShowAddRecord(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Add Record
          </button>
        </div>

        {/* Tabs */}
        <div className="flex space-x-4 mb-6 border-b">
          {['overview', 'medications', 'appointments', 'records'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`pb-2 px-4 capitalize ${
                activeTab === tab
                  ? 'border-b-2 border-blue-600 text-blue-600'
                  : 'text-gray-500 hover:text-gray-700'
              }`}
            >
              {tab}
            </button>
          ))}
        </div>

        {/* Overview Tab */}
        {activeTab === 'overview' && (
          <div className="space-y-6">
            {/* Health Metrics */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h2 className="text-xl font-semibold mb-4">Health Metrics</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {healthMetrics.map((metric, index) => (
                  <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    className="border rounded-lg p-4"
                  >
                    <h3 className="text-sm text-gray-500">{metric.name}</h3>
                    <div className="flex items-baseline mt-2">
                      <span className="text-2xl font-bold">{metric.value}</span>
                      <span className="ml-1 text-gray-500">{metric.unit}</span>
                    </div>
                    <div className="mt-2 text-sm text-green-600">
                      {metric.trend}
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>

            {/* Recent Records */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h2 className="text-xl font-semibold mb-4">Recent Records</h2>
              <div className="space-y-4">
                {[1, 2, 3].map((record) => (
                  <motion.div
                    key={record}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ duration: 0.5, delay: record * 0.1 }}
                    className="flex items-center justify-between p-4 border rounded-lg"
                  >
                    <div>
                      <h3 className="font-medium">Blood Pressure Reading</h3>
                      <p className="text-gray-600">March 10, 2024</p>
                    </div>
                    <div className="text-right">
                      <p className="font-medium">120/80 mmHg</p>
                      <p className="text-sm text-green-600">Normal</p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>
          </div>
        )}

        {/* Medications Tab */}
        {activeTab === 'medications' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Medications</h2>
            <div className="space-y-4">
              {medications.map((med, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="flex items-center justify-between p-4 border rounded-lg"
                >
                  <div>
                    <h3 className="font-medium">{med.name}</h3>
                    <p className="text-gray-600">{med.dosage} - {med.frequency}</p>
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-500">Last taken: {med.lastTaken}</p>
                    <button className="text-blue-600 hover:text-blue-800 mt-2">
                      Mark as Taken
                    </button>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        )}

        {/* Appointments Tab */}
        {activeTab === 'appointments' && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4">Upcoming Appointments</h2>
            <div className="space-y-4">
              {appointments.map((appointment, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className="flex items-center justify-between p-4 border rounded-lg"
                >
                  <div>
                    <h3 className="font-medium">{appointment.doctor}</h3>
                    <p className="text-gray-600">{appointment.type}</p>
                  </div>
                  <div className="text-right">
                    <p className="font-medium">{appointment.date}</p>
                    <p className="text-gray-600">{appointment.time}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default HealthHub;
