import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const Home = () => {
  const features = [
    {
      title: 'Family Dashboard',
      description: 'Stay connected with your family members and track important events.',
      icon: 'üìä',
      path: '/dashboard'
    },
    {
      title: 'Health Hub',
      description: 'Manage health records, medications, and appointments in one place.',
      icon: 'üè•',
      path: '/health'
    },
    {
      title: 'Calendar',
      description: 'Coordinate family events, appointments, and important dates.',
      icon: 'üìÖ',
      path: '/calendar'
    },
    {
      title: 'Tasks',
      description: 'Create and manage family tasks and responsibilities.',
      icon: '‚úÖ',
      path: '/tasks'
    },
    {
      title: 'Communication',
      description: 'Stay in touch with family members through our messaging system.',
      icon: 'üí¨',
      path: '/communication'
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        {/* Hero Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="text-center mb-16"
        >
          <h1 className="text-4xl sm:text-5xl font-bold text-gray-900 mb-6">
            Welcome to FamilyBridge
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Connect, coordinate, and care for your family all in one place.
          </p>
        </motion.div>

        {/* Features Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 mb-16">
          {features.map((feature, index) => (
            <motion.div
              key={feature.title}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <Link
                to={feature.path}
                className="block bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition-shadow duration-300 h-full"
              >
                <div className="flex flex-col items-center text-center">
                  <span className="text-4xl mb-4">{feature.icon}</span>
                  <h2 className="text-xl font-semibold text-gray-900 mb-2">
                    {feature.title}
                  </h2>
                  <p className="text-gray-600">{feature.description}</p>
                </div>
              </Link>
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8 }}
          className="text-center"
        >
          <Link
            to="/dashboard"
            className="inline-block bg-blue-600 text-white px-8 py-4 rounded-lg text-lg font-semibold hover:bg-blue-700 transition-colors duration-300 shadow-md hover:shadow-lg"
          >
            Get Started
          </Link>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
