import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, isToday } from 'date-fns';

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showEventForm, setShowEventForm] = useState(false);

  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const daysInMonth = eachDayOfInterval({ start: monthStart, end: monthEnd });

  const events = [
    { id: 1, date: new Date(2024, 2, 15), title: 'Doctor Appointment', time: '10:00 AM' },
    { id: 2, date: new Date(2024, 2, 20), title: 'Medication Refill', time: '2:00 PM' },
    { id: 3, date: new Date(2024, 2, 25), title: 'Family Meeting', time: '4:00 PM' },
  ];

  const getEventsForDate = (date) => {
    return events.filter(event => 
      format(event.date, 'yyyy-MM-dd') === format(date, 'yyyy-MM-dd')
    );
  };

  const handlePrevMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() - 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(currentDate.getFullYear(), currentDate.getMonth() + 1));
  };

  return (
    <div className="p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Calendar</h1>
          <button
            onClick={() => setShowEventForm(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Add Event
          </button>
        </div>

        {/* Calendar Header */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex justify-between items-center mb-4">
            <button
              onClick={handlePrevMonth}
              className="text-gray-600 hover:text-gray-800"
            >
              ← Previous Month
            </button>
            <h2 className="text-xl font-semibold">
              {format(currentDate, 'MMMM yyyy')}
            </h2>
            <button
              onClick={handleNextMonth}
              className="text-gray-600 hover:text-gray-800"
            >
              Next Month →
            </button>
          </div>

          {/* Calendar Grid */}
          <div className="grid grid-cols-7 gap-2">
            {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
              <div key={day} className="text-center font-semibold py-2">
                {day}
              </div>
            ))}
            {daysInMonth.map((day, index) => {
              const dayEvents = getEventsForDate(day);
              return (
                <motion.div
                  key={index}
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ duration: 0.3, delay: index * 0.02 }}
                  onClick={() => setSelectedDate(day)}
                  className={`
                    p-2 min-h-[100px] border rounded-lg cursor-pointer
                    ${isToday(day) ? 'bg-blue-50 border-blue-200' : 'border-gray-200'}
                    ${isSameMonth(day, currentDate) ? 'bg-white' : 'bg-gray-50'}
                    ${format(selectedDate, 'yyyy-MM-dd') === format(day, 'yyyy-MM-dd') ? 'ring-2 ring-blue-500' : ''}
                  `}
                >
                  <div className="text-sm font-medium mb-1">
                    {format(day, 'd')}
                  </div>
                  <div className="space-y-1">
                    {dayEvents.map(event => (
                      <div
                        key={event.id}
                        className="text-xs bg-blue-100 text-blue-800 rounded px-1 truncate"
                      >
                        {event.time} - {event.title}
                      </div>
                    ))}
                  </div>
                </motion.div>
              );
            })}
          </div>
        </div>

        {/* Selected Date Events */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-xl font-semibold mb-4">
            Events for {format(selectedDate, 'MMMM d, yyyy')}
          </h2>
          <div className="space-y-4">
            {getEventsForDate(selectedDate).length > 0 ? (
              getEventsForDate(selectedDate).map(event => (
                <motion.div
                  key={event.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  className="flex items-center justify-between p-4 border rounded-lg"
                >
                  <div>
                    <h3 className="font-medium">{event.title}</h3>
                    <p className="text-gray-600">{event.time}</p>
                  </div>
                  <button className="text-red-600 hover:text-red-800">
                    Delete
                  </button>
                </motion.div>
              ))
            ) : (
              <p className="text-gray-500">No events scheduled for this day.</p>
            )}
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default Calendar;
